INSERT INTO movies VALUES('{"title":"Combat Shock","year":1986,"cast":["Rick Giovinazzo","Veronica Stork"],"genres":["Drama","War"],"href":"Combat_Shock","extract":"Combat Shock is a 1986 exploitation war drama film written, produced, and directed by Buddy Giovinazzo and starring his brother Rick Giovinazzo in the lead role. The film was distributed by Troma Entertainment.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/b/ba/Combat_Shock_poster.jpg","thumbnail_width":257,"thumbnail_height":388}');
INSERT INTO movies VALUES('{"title":"Night Court","year":1932,"cast":["Walter Huston","Anita Page","Phillips Holmes"],"genres":["Drama","Crime"],"href":"Night_Court_(film)","extract":"Night Court is a 1932 American pre-Code crime film directed by W. S. Van Dyke and written by Bayard Veiller and Lenore Coffee. The film stars Phillips Holmes, Walter Huston, Anita Page, Lewis Stone and Mary Carlisle. The film was released June 4, 1932, by Metro-Goldwyn-Mayer.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/5/54/Night_Court_%28film%29.jpg","thumbnail_width":265,"thumbnail_height":376}');
INSERT INTO movies VALUES('{"title":"Jimmie''s Millions","year":1925,"cast":["Richard Talmadge","Betty Francisco"],"genres":["Adventure","Action","Silent"],"href":"Jimmie%27s_Millions","extract":"Jimmie''s Millions is a 1925 American silent action film directed by James P. Hogan and starring Richard Talmadge, Betty Francisco, and Charles Clary.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/f/f0/Jimmie%27s_Millions_%281925%2C_lobby_card_1%29.jpg/320px-Jimmie%27s_Millions_%281925%2C_lobby_card_1%29.jpg","thumbnail_width":320,"thumbnail_height":251}');
INSERT INTO movies VALUES('{"title":"Tai-Pan","year":1986,"cast":["Bryan Brown","Joan Chen","Tim Guinee"],"genres":["Adventure","Drama"],"href":"Tai-Pan_(film)","extract":"Tai-Pan is a 1986 adventure drama film directed by Daryl Duke, loosely based on James Clavell''s 1966 novel of the same name. While many of the same characters and plot twists are maintained, a few smaller occurrences are left out. Filmed under communist Chinese censorship, some portions of Clavell''s story were considered too offensive to be filmed as written and considerable changes were made.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/d/d2/Tai_pan_poster.jpg","thumbnail_width":256,"thumbnail_height":387}');
INSERT INTO movies VALUES('{"title":"Werewolves Within","year":2021,"cast":["Sam Richardson","Milana Vayntrub","George Basil","Sarah Burns","Michael Chernus","Catherine Curtin","Harvey Guillén","Cheyenne Jackson","Michaela Watkins","Glenn Fleshler"],"genres":["Comedy","Horror","Mystery"],"href":"Werewolves_Within_(film)","extract":"Werewolves Within is a 2021 American mystery comedy horror film directed by Josh Ruben from a screenplay by Mishna Wolff, based on the video game of the same name from Red Storm Entertainment. It stars Sam Richardson, Milana Vayntrub, George Basil, Sarah Burns, Michael Chernus, Catherine Curtin, Wayne Duvall, Harvey Guillén, Rebecca Henderson, Cheyenne Jackson, Michaela Watkins, and Glenn Fleshler, and follows a group of people in a small Vermont town who get trapped in a snowstorm only to suspect one of them is a werewolf.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/3/34/Werewolves_Within_film_poster.png","thumbnail_width":250,"thumbnail_height":370}');
INSERT INTO movies VALUES('{"title":"The Black Rose","year":1950,"cast":["Tyrone Power","Orson Welles"],"genres":["Historical","Adventure"],"href":"The_Black_Rose","extract":"The Black Rose is a 1950 American-British adventure film directed by Henry Hathaway and starring Tyrone Power and Orson Welles."}');
INSERT INTO movies VALUES('{"title":"D-Day","year":2013,"cast":["Arjun Rampal","Irrfan Khan","Huma Qureshi","Rishi Kapoor","Shruti Haasan","Nassar","Sandeep Kulkarni","K. K. Raina","Chandan Roy Sanyal","Imran Hasnee","Maryam Zakaria"],"genres":["Action","Thriller"],"href":"D-Day_(2013_film)","extract":"D-Day is a 2013 Indian Hindi-language action thriller film co-produced by DAR Motion Pictures and Emmay Entertainment. The movie is directed by Nikhil Advani and stars Rishi Kapoor, Irrfan Khan, Arjun Rampal, Huma Qureshi and Shruti Haasan in prominent roles. The film was released on 19 July 2013 to generally positive reception.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/a/a5/D-Day_poster.jpg","thumbnail_width":263,"thumbnail_height":378}');
