INSERT INTO movies VALUES('{"title":"The Lemon Drop Kid","year":1951,"cast":["Bob Hope","Marilyn Maxwell","William Frawley"],"genres":["Comedy","Short"],"href":"The_Lemon_Drop_Kid","extract":"The Lemon Drop Kid is a 1951 American comedy film based on the short story of the same name by Damon Runyon, starring Bob Hope and Marilyn Maxwell. Although Sidney Lanfield is credited as the director, Frank Tashlin reportedly was hired, uncredited, to finish the film. The story had previously been adapted as a 1934 film starring Lee Tracy, with actress Ann Sheridan in a bit part. William Frawley is featured in both versions.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/thumb/e/e8/LemonDropKid.jpg/320px-LemonDropKid.jpg","thumbnail_width":320,"thumbnail_height":250}');
INSERT INTO movies VALUES('{"title":"There''s Always a Woman","year":1938,"cast":["Joan Blondell","Melvyn Douglas","Mary Astor"],"genres":["Comedy","Mystery"],"href":"There%27s_Always_a_Woman","extract":"There''s Always a Woman is a 1938 American comedy mystery film directed by Alexander Hall and starring Joan Blondell and Melvyn Douglas. Seeing the potential for a series, Columbia Pictures quickly made a sequel, There''s That Woman Again, released the same year, with Douglas reprising his role, but with Virginia Bruce as Sally. No further sequels were made.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/thumb/3/3c/There%27s_Always_a_Woman.jpg/320px-There%27s_Always_a_Woman.jpg","thumbnail_width":320,"thumbnail_height":251}');
