INSERT INTO movies VALUES('{"title":"Enchanted","year":2007,"cast":["Amy Adams","Patrick Dempsey","James Marsden","Timothy Spall","Idina Menzel","Susan Sarandon","Rachel Covey","Elizabeth Mathis","Tonya Pinkins","Isiah Whitlock Jr.","Tibor Feldman","Jodi Benson","Matt Servitto","John Rothman","Marlon Saunders","Michaela Conlin","Paige O''Hara","Danny Mastrogiorgio","Judy Kuhn","Joseph Siravo","Margaret Travolta","Jon McLaughlin","Helen Stenborg","Jeff Bennett","Kevin Lima","Emma Rose Lima","Teala Dunn","Fred Tatasciore","Julie Andrews"],"genres":["Live Action","Animated","Comedy","Fantasy","Musical","Romance"],"href":"Enchanted_(film)","extract":"Enchanted is a 2007 American live-action/animated musical fantasy romantic comedy film directed by Kevin Lima and written by Bill Kelly. Co-produced by Walt Disney Pictures, Josephson Entertainment, and Right Coast Productions, the film stars Amy Adams, Patrick Dempsey, James Marsden, Timothy Spall, Idina Menzel, Rachel Covey, and Susan Sarandon, with Julie Andrews as the narrator. It focuses on an archetypal Disney princess-to-be exiled from her animated world into the live-action world of New York City.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/2/2a/Enchantedposter.jpg","thumbnail_width":259,"thumbnail_height":383}');
INSERT INTO movies VALUES('{"title":"Thunderbolt''s Tracks","year":1927,"cast":["Jack Perrin","Pauline Curley"],"genres":["Western","Silent"],"href":"Thunderbolt%27s_Tracks","extract":"Thunderbolt''s Tracks is a 1927 American silent Western film directed by J.P. McGowan and starring Jack Perrin, Pauline Curley and Buzz Barton.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/b/bb/Thunderbolt%27s_Tracks.jpg","thumbnail_width":258,"thumbnail_height":386}');
INSERT INTO movies VALUES('{"title":"Whose Life Is It Anyway?","year":1981,"cast":["Richard Dreyfuss","John Cassavetes","Christine Lahti","Thomas Carter","Kenneth McMillan"],"genres":["Drama"],"href":"Whose_Life_Is_It_Anyway%3F_(1981_film)","extract":"Whose Life Is It Anyway? is a 1981 American drama film directed by John Badham and starring Richard Dreyfuss. It was adapted by Brian Clark and Reginald Rose from Clark''s 1972 television movie and 1978 stage play, all under the same title. Whose Life Is It Anyway? received a 70% fresh rating on Rotten Tomatoes, based on 10 reviews and an average rating of 6.58/10.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/9/95/Whose_Life_is_it_Anyway%3F_poster.jpg","thumbnail_width":256,"thumbnail_height":388}');
INSERT INTO movies VALUES('{"title":"The Garment Jungle","year":1957,"cast":["Lee J. Cobb","Kerwin Mathews","Gia Scala"],"genres":["Noir","Crime"],"href":"The_Garment_Jungle","extract":"The Garment Jungle is a 1957 American film noir crime film directed by Vincent Sherman and starring Lee J. Cobb, Kerwin Mathews, Gia Scala, Richard Boone and Valerie French.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/2/2c/The_Garment_Jungle_film_poster.jpg","thumbnail_width":252,"thumbnail_height":394}');
