INSERT INTO movies VALUES('{"title":"Fever Pitch","year":2005,"cast":["Drew Barrymore","Jimmy Fallon","James B. Sikking","JoBeth Williams","Jason Spevack","Jack Kehler","Lenny Clarke","Ione Skye","Siobhan Fallon Hogan","KaDee Strickland","Marissa Jaret Winokur","Evan Helmuth","Zen Gesner","Jackie Burroughs","Stephen King","Kris Williams","Steve Levy","Willie Garson","Armando Riesco","Brett Murphy","Andrew Wilson","Johnny Damon","Trot Nixon","Jason Varitek","Jim Rice","Joe Castiglione","Don Orsillo","Dennis Eckersley"],"genres":["Romance","Comedy","Drama"],"href":"Fever_Pitch_(2005_film)","extract":"Fever Pitch is a 2005 American romantic comedy-drama film directed by the Farrelly brothers. It stars Drew Barrymore and Jimmy Fallon and is a remake of the British 1997 film of the same title. Nick Hornby, who had written the original 1992 book and the 1997 screenplay adaptation, acted as an executive producer for the American remake.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/6/6d/Fever_Pitch_US.jpg","thumbnail_width":238,"thumbnail_height":350}');
