INSERT INTO movies VALUES('{"title":"Pirates of the Sky","year":1926,"cast":["Wanda Hawley","Crauford Kent"],"genres":["Action","Adventure","Drama","Silent"],"href":"Pirates_of_the_Sky","extract":"Pirates of the Sky is a 1926 American silent adventure melodrama film directed by Charles Andrews. The film stars Charles Hutchison, Wanda Hawley and Crauford Kent. In different sources, Pirates of the Sky distributed by Pathé Exchange has conflicting release dates of February 20, 1926 and March 21, 1927.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/thumb/2/20/Pirates_of_the_Sky.jpg/320px-Pirates_of_the_Sky.jpg","thumbnail_width":320,"thumbnail_height":226}');
INSERT INTO movies VALUES('{"title":"Passers By","year":1920,"cast":["Herbert Rawlinson","Ellen Cassidy"],"genres":["Drama","Silent","Romance"],"href":"Passers_By_(1920_film)","extract":"Passers By is a 1920 American silent romantic drama film directed by J. Stuart Blackton and starring Herbert Rawlinson, Leila Valentine and Ellen Burford. It was based on a 1911 West End play of the same title by C. Haddon Chambers, which had previously been made into the 1916 film Passers By.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/d/d7/Herbert_Rawlinson_in_Passers_By_1_by_J._Stuart_Blackton_Film_Daily_1920.png/320px-Herbert_Rawlinson_in_Passers_By_1_by_J._Stuart_Blackton_Film_Daily_1920.png","thumbnail_width":320,"thumbnail_height":211}');
INSERT INTO movies VALUES('{"title":"Paradise for Three","year":1938,"cast":["Robert Young","Mary Astor","Frank Morgan"],"genres":["Comedy","Romance"],"href":"Paradise_for_Three","extract":"Paradise for Three, titled Romance for Three in the United Kingdom, is a 1938 romantic comedy film starring Frank Morgan as a wealthy industrialist who decides to find out about his German workers by temporarily living among them incognito. It was adapted from Erich Kästner''s novel Three Men in the Snow, published in 1934.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/thumb/1/16/Paradise_for_Three_FilmPoster.jpeg/320px-Paradise_for_Three_FilmPoster.jpeg","thumbnail_width":320,"thumbnail_height":248}');
