INSERT INTO movies VALUES('{"title":"Boothill Brigade","year":1937,"cast":["Johnny Mack Brown","Dick Curtis"],"genres":["Western"],"href":"Boothill_Brigade","extract":"Boothill Brigade is a 1937 American Western film directed by Sam Newfield and written by George H. Plympton. The film stars Johnny Mack Brown, Claire Rochelle, Dick Curtis, Horace Murphy, Frank LaRue and Ed Cassidy. The film was released on August 2, 1937, by Republic Pictures.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/f/f3/Boothill_Brigade_poster.jpg","thumbnail_width":263,"thumbnail_height":378}');
