INSERT INTO movies VALUES('{"title":"Promised Land","year":2012,"cast":["Matt Damon","John Krasinski","Rosemarie DeWitt","Frances McDormand"],"genres":["Drama"],"href":"Promised_Land_(2012_film)","extract":"Promised Land is a 2012 American drama film directed by Gus Van Sant and starring Matt Damon, John Krasinski, Frances McDormand, Rosemarie DeWitt and Hal Holbrook. The screenplay is written by Damon and Krasinski based on a story by Dave Eggers. Promised Land follows two petroleum landmen who visit a rural town in an attempt to buy drilling rights from the local residents.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/a/af/Promised_Land_%282012%29.png","thumbnail_width":260,"thumbnail_height":385}');
