INSERT INTO movies VALUES('{"title":"Never the Twain Shall Meet","year":1931,"cast":["Leslie Howard","Conchita Montenegro","Karen Morley"],"genres":["Drama"],"href":"Never_the_Twain_Shall_Meet_(1931_film)","extract":"Never the Twain Shall Meet is a 1931 American drama film produced and distributed by Metro-Goldwyn-Mayer and starring Leslie Howard and Conchita Montenegro. It is based on the novel of the same title by Peter B. Kyne. The film was directed by W. S. Van Dyke and was filmed in Tahiti like Van Dyke''s two previous south sea adventures The Pagan and White Shadows in the South Seas. The film is a remake of a 1925 silent film of the same name.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/f/f7/Never_the_Twain_Shall_Meet_%281931_film%29.jpg","thumbnail_width":256,"thumbnail_height":390}');
