INSERT INTO movies VALUES('{"title":"Don''t Get Personal","year":1942,"cast":["Jane Frazee","Anne Gwynne"],"genres":["Comedy","Musical","Romance"],"href":"Don%27t_Get_Personal_(1942_film)","extract":"Don''t Get Personal is a 1942 American romantic musical film directed by Charles Lamont and starring Jane Frazee."}');
INSERT INTO movies VALUES('{"title":"Marines, Let''s Go","year":1961,"cast":["Tom Tryon","David Hedison"],"genres":["War"],"href":"Marines,_Let%27s_Go","extract":"Marines, Let''s Go is a 1961 CinemaScope DeLuxe Color Korean War film about three Marine buddies on shore leave in Japan and at war in Korea. It was produced and directed by Raoul Walsh, who also wrote the story. Walsh had previously had successes with films about the U.S. Marine Corps in World War I, the 1920s, and World War II. This was the next-to-last film of Walsh''s long directing career.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/thumb/0/00/MarinesLetsGo.jpg/320px-MarinesLetsGo.jpg","thumbnail_width":320,"thumbnail_height":245}');
INSERT INTO movies VALUES('{"title":"The Thing","year":1982,"cast":["Kurt Russell","Keith David","Wilford Brimley"],"genres":["Horror","Science Fiction"],"href":"The_Thing_(1982_film)","extract":"The Thing is a 1982 American science fiction horror film directed by John Carpenter from a screenplay by Bill Lancaster. Based on the 1938 John W. Campbell Jr. novella Who Goes There?, it tells the story of a group of American researchers in Antarctica who encounter the eponymous \"Thing\", a parasitic extraterrestrial life-form that assimilates, then imitates, other organisms. The group is overcome by paranoia and conflict as they learn that they can no longer trust each other and that any of them could be the Thing. The film stars Kurt Russell as the team''s helicopter pilot R.J. MacReady, with A. Wilford Brimley, T. K. Carter, David Clennon, Keith David, Richard Dysart, Charles Hallahan, Peter Maloney, Richard Masur, Donald Moffat, Joel Polis, and Thomas G. Waites in supporting roles.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/a/a6/The_Thing_%281982%29_theatrical_poster.jpg","thumbnail_width":220,"thumbnail_height":338}');
INSERT INTO movies VALUES('{"title":"The World of Henry Orient","year":1964,"cast":["Peter Sellers","Paula Prentiss","Angela Lansbury","Phyllis Thaxter","Tom Bosley"],"genres":["Comedy"],"href":"The_World_of_Henry_Orient","extract":"The World of Henry Orient is a 1964 American comedy film directed by George Roy Hill and starring Peter Sellers, Paula Prentiss, Angela Lansbury, Tippy Walker, Merrie Spaeth, Phyllis Thaxter, Bibi Osterwald and Tom Bosley. It is based on the novel of the same name by Nora Johnson, who co-wrote the screenplay with her father, Nunnally Johnson.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/4/40/WORLDOFH-00AA1-poster_hires.jpg","thumbnail_width":258,"thumbnail_height":387}');
