INSERT INTO movies VALUES('{"title":"Caravan","year":1934,"cast":["Charles Boyer","Loretta Young","Jean Parker"],"genres":["Drama","Musical"],"href":"Caravan_(1934_film)","extract":"Caravan  is a 1934 American musical film released by Fox Film Corporation, directed by Erik Charell, and starring Charles Boyer, Loretta Young, Phillips Holmes and Jean Parker. Fox also produced a French language version of this film, Caravane (1934) starring Boyer, Annabella, and Conchita Montenegro, with Lou Tellegen in a small role.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/a/ae/Caravan_%281934_film%29.jpg","thumbnail_width":249,"thumbnail_height":350}');
INSERT INTO movies VALUES('{"title":"Passengers","year":2008,"cast":["Anne Hathaway","Patrick Wilson","Clea DuVall","Andre Braugher","Chelah Horsdal","David Morse","Dianne Wiest","William B. Davis","Ryan Robbins","Don Thompson","Stacy Grant","Brad Turner","Claire Smithies"],"genres":["Mystery","Thriller","Romance"],"href":"Passengers_(2008_film)","extract":"Passengers is a 2008 romantic mystery thriller film directed by Rodrigo García, written by Ronnie Christensen, and starring Anne Hathaway and Patrick Wilson. It was released in the United States by TriStar Pictures on October 24, 2008.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/5/55/Passengersposter08.jpg","thumbnail_width":260,"thumbnail_height":384}');
INSERT INTO movies VALUES('{"title":"Tangled Lives","year":1917,"cast":["Genevieve Hamper","Stuart Holmes"],"genres":["Drama","Silent"],"href":"Tangled_Lives_(1917_film)","extract":"Tangled Lives is a 1917 American silent drama film produced and distributed by the Fox Film Corporation. The film was directed by J. Gordon Edwards and starred husband and wife stage actors Genevieve Hamper and Robert B. Mantell.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/c/c7/Tangled_Lives_poster.jpg/320px-Tangled_Lives_poster.jpg","thumbnail_width":320,"thumbnail_height":472}');
