INSERT INTO movies VALUES('{"title":"Robbers'' Roost","year":1933,"cast":["George O''Brien","Maureen O''Sullivan"],"genres":["Western","War"],"href":"Robbers%27_Roost_(1933_film)","extract":"Robbers'' Roost is a 1932 American Pre-Code Western film directed by David Howard and Louis King and written by Dudley Nichols. The film stars George O''Brien and Maureen O''Sullivan. It is based on the novel Robbers'' Roost by Zane Grey. The film premiered in September 13 to early November or December 30, 1932, and was released on January 1, 1933, by Fox Film Corporation.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/3/3e/Robbers%27_Roost_poster.jpg","thumbnail_width":257,"thumbnail_height":387}');
INSERT INTO movies VALUES('{"title":"Missing in Action 2: The Beginning","year":1985,"cast":["Chuck Norris","Soon-Tek Oh","Steven Williams"],"genres":["Action","Adventure"],"href":"Missing_in_Action_2:_The_Beginning","extract":"Missing in Action 2: The Beginning is a 1985 American action adventure film, and a prequel to Missing in Action, both of which star Chuck Norris. It was directed by Lance Hool, and written by Steve Bing, Larry Levinson and Arthur Silver. It is the second installment in the Missing in Action film series.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/3/31/Missing_in_action_2.jpg","thumbnail_width":252,"thumbnail_height":396}');
INSERT INTO movies VALUES('{"title":"Theremin: An Electronic Odyssey","year":1993,"cast":["Footage of","Leon Theremin","Brian Wilson"],"genres":["Documentary"],"href":"Theremin:_An_Electronic_Odyssey","extract":"Theremin: An Electronic Odyssey is a 1993 documentary film directed by Steven M. Martin about the life of Leon Theremin and his invention, the theremin, a pioneering electronic musical instrument. It follows his life, including being imprisoned in a Soviet Gulag, and the influence of his instrument, which came to define the sound of eerie in 20th-century movies, and influenced popular music as it searched for and celebrated electronic music in the 1960s. It was first broadcast in November 1993 as a special edition of Channel 4''s Without Walls arts strand.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/e/ea/Theremin-_An_Electronic_Odyssey_FilmPoster.jpeg","thumbnail_width":258,"thumbnail_height":387}');
