INSERT INTO movies VALUES('{"title":"The Dream Cheater","year":1920,"cast":["J. Warren Kerrigan","Wedgwood Nowell"],"genres":["Horror","Silent"],"href":"The_Dream_Cheater","extract":"The Dream Cheater is a 1920 American silent horror film directed by Ernest C. Warde and starring J. Warren Kerrigan, Wedgwood Nowell and Fritzi Brunette. It is based on the 1831 novel La Peau de chagrin by Honoré de Balzac.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/4/4e/The_Dream_Cheater_%281920%29_-_Ad_2.jpg/320px-The_Dream_Cheater_%281920%29_-_Ad_2.jpg","thumbnail_width":320,"thumbnail_height":428}');
