INSERT INTO movies VALUES('{"title":"The September Issue","year":2009,"cast":["Anna Wintour","Grace Coddington","John Galliano","Oscar de la Renta","Thakoon Panichgul","Stefano Pilati","Jean-Paul Gaultier","Nicolas Ghesquière","Jane Thompson","Isabel Toledo","Karl Lagerfeld","Vera Wang","Coco Rocha","Sienna Miller","Caroline Trentini","Chanel Iman","Daria Werbowy","Lily Donaldson","Raquel Zimmermann","Lily Cole","Hilary Rhoda","Patrick Demarchelier","Steven Klein","Craig McDean","Mario Testino","David Sims","Hamish Bowles","Edward Enninful","Si Newhouse","André Leon Talley","Robert Verdi"],"genres":["Documentary"],"href":"The_September_Issue","extract":"The September Issue is a 2009 American documentary film directed by R.J. Cutler about the behind-the-scenes drama that follows editor-in-head Anna Wintour and her staff during the production of the September 2007 issue of American Vogue magazine.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/e/e7/The_september_issue.jpg","thumbnail_width":220,"thumbnail_height":326}');
INSERT INTO movies VALUES('{"title":"Virtuous Wives","year":1918,"cast":["Anita Stewart","Conway Tearle","Hedda Hopper"],"genres":["Drama","Silent"],"href":"Virtuous_Wives","extract":"Virtuous Wives is a lost 1918 American silent drama film directed by George Loane Tucker, and stars Anita Stewart. Future gossip columnist Hedda Hopper co-starred. Based on the novel of the same name by Owen Johnson, the film was produced Anita Stewart''s, production company. It was also the first film produced by Louis B. Mayer.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/0/0f/Virtuous_Wives_Anita_Stewart_3_1918.png/320px-Virtuous_Wives_Anita_Stewart_3_1918.png","thumbnail_width":320,"thumbnail_height":434}');
INSERT INTO movies VALUES('{"title":"Desert Bloom","year":1986,"cast":["Annabeth Gish","Jon Voight","Ellen Barkin"],"genres":["Drama"],"href":"Desert_Bloom_(film)","extract":"Desert Bloom is a 1986 American drama film directed by Eugene Corr and starring an ensemble cast led by Jon Voight and JoBeth Williams. It was screened in the Un Certain Regard section at the 1986 Cannes Film Festival and funded through the Sundance Film Festival Institute.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/1/16/DesertBloomJoBeth.jpg","thumbnail_width":257,"thumbnail_height":387}');
