INSERT INTO movies VALUES('{"title":"Abilene Trail","year":1951,"cast":["Whip Wilson","Noel Neill"],"genres":["Western"],"href":"Abilene_Trail_(film)","extract":"Abilene Trail is a 1951 American Western film directed by Lewis D. Collins and starring Whip Wilson, Noel Neill and Andy Clyde.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/thumb/6/65/Abilene_Trail_%28film%29.jpg/320px-Abilene_Trail_%28film%29.jpg","thumbnail_width":320,"thumbnail_height":248}');
INSERT INTO movies VALUES('{"title":"In Search of Gregory","year":1969,"cast":["Julie Christie","Michael Sarrazin","John Hurt"],"genres":["Drama"],"href":"In_Search_of_Gregory","extract":"In Search of Gregory is a 1969 British-Italian drama film directed by Peter Wood and starring Julie Christie.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/f/f3/In_Search_of_Gregory.jpg","thumbnail_width":258,"thumbnail_height":386}');
INSERT INTO movies VALUES('{"title":"The Last of the Duanes","year":1930,"cast":["George O''Brien","Lucile Browne"],"genres":["Western"],"href":"The_Last_of_the_Duanes_(1930_film)","extract":"The Last of the Duanes is a 1930 American pre-Code Western film produced and released by Fox Film Corporation, directed by Alfred L. Werker, and starring George O''Brien, Lucile Browne and Myrna Loy."}');
INSERT INTO movies VALUES('{"title":"The Fighting Hope","year":1915,"cast":["George Gebhardt","Laura Hope Crews"],"genres":["Drama","Silent"],"href":"The_Fighting_Hope","extract":"The Fighting Hope is a 1915 silent film drama directed by George Melford and starring Thomas Meighan and Laura Hope Crews, both in their film debuts. Jesse Lasky produced and Paramount Pictures released. Based on a 1908 play by William J. Hurlbut that was produced by David Belasco.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/c/c4/The_Fighting_Hope_-_newspaperscene_-_1915.jpg/320px-The_Fighting_Hope_-_newspaperscene_-_1915.jpg","thumbnail_width":320,"thumbnail_height":225}');
INSERT INTO movies VALUES('{"title":"First to Fight","year":1967,"cast":["Chad Everett","Dean Jagger","Gene Hackman"],"genres":["War"],"href":"First_to_Fight_(film)","extract":"First to Fight is a 1967 American Technicolor Warner Bros. war film starring Chad Everett, Marilyn Devin, making her film debut, Dean Jagger, Bobby Troup and James Best. Based loosely on the story of United States Marine Gunnery Sergeant John \"Manila\" Basilone, who later went back into action and died at Iwo Jima.","thumbnail":"https://upload.wikimedia.org/wikipedia/en/c/c6/First_to_Fight.jpg","thumbnail_width":214,"thumbnail_height":397}');
INSERT INTO movies VALUES('{"title":"Vanity Fair","year":1923,"cast":["Mabel Ballin","Hobart Bosworth","George Walsh"],"genres":["Drama","Silent"],"href":"Vanity_Fair_(1923_film)","extract":" Vanity Fair (1923) is a silent feature film directed by Hugo Ballin and released by Samuel Goldwyn.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/b/b9/Vanity_Fair_lobby_card_1923.JPG/320px-Vanity_Fair_lobby_card_1923.JPG","thumbnail_width":320,"thumbnail_height":250}');
INSERT INTO movies VALUES('{"title":"The Lure of Youth","year":1921,"cast":["Cleo Madison","Gareth Hughes","William Conklin"],"genres":["Romance","Silent"],"href":"The_Lure_of_Youth","extract":"The Lure of Youth is a 1921 American silent romance film, directed by Phil Rosen. It stars Cleo Madison, William Conklin, and Gareth Hughes, and was released on January 10, 1921.","thumbnail":"https://upload.wikimedia.org/wikipedia/commons/thumb/c/ce/The_Lure_of_Youth_%281921%29_-_Ad_1.jpg/320px-The_Lure_of_Youth_%281921%29_-_Ad_1.jpg","thumbnail_width":320,"thumbnail_height":340}');
